  #{creatorUserId,jdbcType=DECIMAL}, #{costPrice,jdbcType=DECIMAL}, #{suggestedPrice,jdbcType=DECIMAL}, 
  #{developmentTeam,jdbcType=VARCHAR}, to_date(#{developmentTime},'yyyy-MM-dd HH24-mi-ss'), #{link,jdbcType=VARCHAR}, 
  to_date(#{createTime},'yyyy-MM-dd HH24-mi-ss'), to_date(#{updateTime},'yyyy-MM-dd HH24-mi-ss'), #{description,jdbcType=VARCHAR}
  )
